    int rec(vector<vector<int>>&dp, string &s, string &t, int i, int j)
    {
        if(i==s.length()) {
            return t.length()-j;
        }
        else if(j==t.length()){
            return s.length()-i;
        }

        if(dp[i][j]==-1){
            int step1=INT_MAX, step2=INT_MAX;
            if(s[i]==t[j])
            {
                step1 = min(rec(dp,s,t,i+1,j+1),min(1+rec(dp,s,t,i+1,j),1+rec(dp,s,t,i,j+1)));
            }
            else 
            {
                step2 = min(1+rec(dp,s,t,i+1,j),1+rec(dp,s,t,i,j+1));
            }
            dp[i][j]=min(step1,step2);
        }
        return dp[i][j];
