	class Solution {
	public:
		int f(int i,int j,string& w1,string& w2){
			if(i<0 || j<0) return 0;
	//      Match
			if(w1[i]==w2[j]) return 1+f(i-1,j-1,w1,w2);
	//      NotMatch   
			return max(f(i-1,j,w1,w2),f(i,j-1,w1,w2));
		}

		int minDistance(string w1, string w2) {
			int n=w1.size();
			int m=w2.size();
			return n+m-2*f(n-1,m-1,w1,w2);
		}
	};

