			for(int i=1;i<=n;i++){
				for(int j=1;j<=m;j++){
					if(w1[i-1]==w2[j-1]) dp[i][j]=1+dp[i-1][j-1];
					else dp[i][j]=max(dp[i-1][j],dp[i][j-1]);
