			int n=w1.size(),m=w2.size();
			vector<int> prev(m+1,0),curr(m+1,0);
			for(int i=1;i<=n;i++){
				for(int j=1;j<=m;j++){
					if(w1[i-1]==w2[j-1]) curr[j]=1+prev[j-1];
					else curr[j]=max(prev[j],curr[j-1]);
				}
				prev=curr;
			}
			return n+m-2*prev[m];
		}
	};

