class Solution {
    public ListNode rotateRight(ListNode head, int k) {
        if(head == null || head.next == null)
            return head;
        ListNode count = head; // counting the list size
        int size = 0;
        while(count!=null){
            size++;
            count = count.next;
        }
        k=k%size;  // adjusting number of rotations
        if(k == 0)
            return head;
        ListNode x = head; // setting the pointer to the position of breaking the list
        int a=0;
        while(x != null){
            a++;
            if(a == (size-k)){
                break;
            }
            x=x.next;
        }
        ListNode newList = new ListNode(-1); // New listnode to point the head of the answer list
        newList.next = x.next;                //
        ListNode newx = x.next;              //
        x.next = null;                      //
        while(newx.next != null){          //
            newx=newx.next;               //
        }                                //
        newx.next = head;               //  setting up the new list
        return newList.next;
    }
}
