    ListNode* rotateRight(ListNode* head, int k) {
        if(!head || !head->next || k==0) return head; // STEP 1  BASE CASE   
        int n = lengthofll(head);                     // STEP 2  FINDING NO OF NODES
        if(n==k || k%n==0) return head;               // STEP 3  AGAIN CHECKING REFER IMAGES FOR WHY
        int k1;                                      //  STEP 4  SIMPLIFYING K  
