struct ListNode* rotateRight(struct ListNode* head, int k){
     if (!head || !head->next || !k ) return head;

    // find the tail, compute the len to find k.
    // since were not iterate the whole list to NULL
    // len starts from 1.
    int len = 1;
    struct ListNode *curr = head;
    while (curr->next != NULL) {
        len++;
        curr = curr->next;
    }
    // Now curr points to last node, circle the list.
    curr->next = head;
    // Find the rotate position.
    int position = len - k % len;
    for (int i = 0; i < position; i++) {
        // Since list is circled, move the curr to position.
        curr = curr->next;
    } 
    // Since the list is circled, change head to (position+1)th node.
    head = curr->next;
    // Change the next of curr node to NULL to uncircle the list.
    curr->next = NULL;
    return head;
}
