class Solution {
    func rotateRight(_ head: ListNode?, _ k: Int) -> ListNode? {

        if head == nil { return nil }

        var tail: ListNode?
        var node = head
        var count = 0
        while node != nil {
            tail = node
            node = node!.next
            count += 1
        }

