public ListNode rotateRight(ListNode head, int k) {
    if (head == null) return head;
    k = k % getLen(head);
    ListNode p1 = new ListNode(0);
    ListNode p2 = new ListNode(0);
    p1.next = head;
    p2.next = head;
    for (int i = 0; i < k; i++) {
        p2 = p2.next;
    }
    while (p2.next != null) {
        p1 = p1.next;
        p2 = p2.next;
    }
    p2.next = head;
    ListNode res = p1.next;
    p1.next = null;
    return res;
}

private int getLen(ListNode node) {
    int count = 0;
    while (node != null) {
        count++;
        node = node.next;
    }
    return count;
