    while (p2.next != null) {
        p1 = p1.next;
        p2 = p2.next;
    }
    p2.next = head;
    ListNode res = p1.next;
    p1.next = null;
    return res;
}

private int getLen(ListNode node) {
    int count = 0;
    while (node != null) {
        count++;
        node = node.next;
    }
    return count;
}

