	if len(trees) == 1 {
		return trees
	}

	upper, lower := make([][]int, 0), make([][]int, 0)

	sort.Slice(trees, func(q, p int) bool {
		if trees[q][0]-trees[p][0] == 0 {
			return trees[q][1] < trees[p][1]
		}

		return trees[q][0] < trees[p][0]
	})

	for i := 0; i < len(trees); i++ {
