    function cmp (p1, p2, p3) {
        let [x1, y1] = p1;
        let [x2, y2] = p2;
        let [x3, y3] = p3;
        return (y3 - y2)*(x2 - x1) - (y2 - y1)*(x3 - x2);
    }

