    public class Solution
    {
        private static int CalculateOrientation((int, int) p, (int, int) q, (int, int) r)
        {
            int val = (q.Item2 - p.Item2) * (r.Item1 - q.Item1) -
                      (q.Item1 - p.Item1) * (r.Item2 - q.Item2);
            if (val == 0)
            {
                return 0; // collinear 
            }

            return (val > 0) ? 1 : 2; // clock or counterclock wise
        }

        private static ISet<(int, int)> Jarvis(IList<(int, int)> points)
        {
            if (points.Count < 3)
            {
                return new HashSet<(int, int)>();
            }

            ISet<(int, int)> res = new HashSet<(int, int)>();


            int leftmostPointIdx = 0;
            for (int i = 1; i < points.Count; i++)
            {
                if (points[i].Item1 < points[leftmostPointIdx].Item1)
                {
                    leftmostPointIdx = i;
                }
            }

            int idx = leftmostPointIdx;
            do
            {
                int next = (idx + 1) % points.Count;

                for (int i = 0; i < points.Count; i++)
                {
                    if (CalculateOrientation(points[idx], points[i], points[next]) == 2)
                    {
                        next = i;
                    }
                }

                for (int i = 0; i < points.Count; i++)
                {
                    if (CalculateOrientation(points[idx], points[i], points[next]) == 0)
                    {
                        res.Add(points[i]);
                    }
                }


                idx = next;

            } while (idx != leftmostPointIdx);

            return res;
        }

        public int[][] OuterTrees(int[][] points)
        {
            if (points.Length <= 3)
            {
                return points;
            }

            return Jarvis(points.Select(p => (p[0], p[1])).ToList())
                .Select(p => new[] { p.Item1, p.Item2 }).ToArray();
        } 
    }
