class Solution:
    def outerTrees(self, trees: List[List[int]]) -> List[List[int]]:
        hull = []

        # Sort trees by x-coordinate first, and then y-coordinate (in case of ties)
        trees.sort(key=lambda x: (x[0], x[1]))

        def cross(p: List[int], q: List[int], r: List[int]) -> int:
            # Simplify the cross product calculation using tuple unpacking
            px, py = p
            qx, qy = q
            rx, ry = r
            return (qy - py) * (rx - qx) - (qx - px) * (ry - qy)

        # Build lower hull: left-to-right scan
        for tree in trees:
            # Use a while loop instead of a for loop to simplify the code
            while len(hull) > 1 and cross(hull[-2], hull[-1], tree) < 0:
                hull.pop()
            hull.append(tree)

        # Build upper hull: right-to-left scan
        for tree in reversed(trees):
            while len(hull) > 1 and cross(hull[-2], hull[-1], tree) < 0:
                hull.pop()
            hull.append(tree)

        # Remove duplicates and return the hull as a list of lists
        return [list(x) for x in set(tuple(x) for x in hull)]

