	class Solution {
	public:
		int angle(vector<int>& A, vector<int>& B, vector<int>& C){

		// A[x1,y1] , B[x2,y2] , C[x3,y3]
		// A[0,1]     B[0,1]    C[0,1]
		// AB = [ x2-x1 , y2-y1]  BC = [ x3-x2, y3-y2 ]
		// Cross product of AB and BC = ( X1*Y2 - X2*Y1 )
		// crossproduct of AB and BC = ((x2-x1)*(y3-y2) - (x3-x2)*(y2-y1))

		return ( (B[0]-A[0])*(C[1]-B[1]) - (C[0]-B[0])*(B[1]-A[1])) ;
