def outerTrees(self, trees: List[List[int]]) -> List[List[int]]:
	origin = [float('inf'), float('inf')]
    for x, y in trees:
		if x < origin[0] or y < origin[1]:
			origin = [x, y]

	def clockwise(a, b, c):
		return (c[1] - b[1]) * (b[0] - a[0]) < (c[0] - b[0]) * (b[1] - a[1]) 

	trees.sort(key=lambda p: (atan2(p[1] - origin[1], p[0] - origin[0]), -p[1], p[0]))
    stack = [origin]
    for x, y in trees:
		if [x, y] != origin:
			while len(stack) > 1 and clockwise(stack[-2], stack[-1], [x, y]):
				stack.pop()
			stack.append((x, y))     
	return stack     
