class Solution:

    def outerTrees(self, trees: List[List[int]]) -> List[List[int]]:
        ret = set()

        if (len(trees) <= 3):
            return trees

        # find the leftmost point
        leftmost = 0
        for i, tree in enumerate(trees):
            if(tree[0] < trees[leftmost][0]):
                leftmost = i

        i = leftmost
        while True:
            # j as next point
            j = (i + 1) % len(trees)

            # find the corresponding rightmost point
            for k, tree in enumerate(trees):
                if (self.crossProduct(trees[i], trees[k], trees[j]) > 0):
                    j = k

            ret.add((trees[j][0], trees[j][1]))
            #print(" ADD ", j)

            # get points on the same straight line
            for k, tree in enumerate(trees):

                if (k != i) and (k != j) and (self.crossProduct(trees[i], trees[k], trees[j]) == 0):

                    ret.add((trees[k][0], trees[k][1]))
                    #print(" ADD ", k)

                    # go to the farthest point 
                    if (dist(trees[i], trees[k]) > dist(trees[i], trees[j])):
                        #print(" MOVE ", j , " to ", k)
                        j = k

            i = j

            if(i == leftmost):
                break

        return [list(item) for item in ret]

    def crossProduct(self, A: List[int], B: List[int], C: List[int]) -> int:
        # AB X BC = (Xb-Xa) * (Yc-Yb) - (Xc-Xb) * (Yb- Ya)
        return (B[0] - A[0]) * (C[1] - B[1]) - (C[0] - B[0]) * (B[1] - A[1]) 

    def dist(self, A: List[int], B: List[int]) -> int:
        # distance square
        return ( (B[1] - A[1]) * (B[0] - B[1]) )**2
