class Solution {
	public int[][] outerTrees(int[][] trees) {
		int n=trees.length;
		int[] start=null;
		for(int []t:trees){
			if(start==null || start[0]>t[0])	start=t;
		}
		int curr[]=start;
		Set<int []> res=new HashSet<>();
		res.add(curr);
		while(true){
			List<int []> colinear=new ArrayList<>();
			int next[]=trees[0];
			for(int i=1;i<n;i++){
				if(curr!=trees[i]){
					int val=crossProduct(curr,next,trees[i]);
					if(val>0){
						next=trees[i];
						colinear.clear();
					}
					else if(val==0){
						if(distance(curr,next,trees[i])<0){
							colinear.add(next);
							next=trees[i];
						}
						else colinear.add(trees[i]);
					}
				}
			}
			for(int i[]:colinear)	res.add(i);
			if(next==start)	break;
			res.add(next);
			curr=next;
		}
		int fencePoints[][]=new int[res.size()][2],k=0;
		for(int i[]:res)    fencePoints[k++]=i;
		return fencePoints;
	}
	private static int crossProduct(int curr[],int []next,int []point){
		int x1=curr[0]-next[0];
		int x2=curr[0]-point[0];
		int y1=curr[1]-next[1];
		int y2=curr[1]-point[1];
		return y2*x1-y1*x2;
	}
	private static int distance(int curr[],int []next,int []point){
		int x1=curr[0]-next[0];
		int x2=curr[0]-point[0];
		int y1=curr[1]-next[1];
		int y2=curr[1]-point[1];
		return y1*y1+x1*x1-y2*y2-x2*x2;
	}
}

