class Solution {
    public List<Point> outerTrees(Point[] points) {
        if (points.length == 1) {
            List<Point> res = new ArrayList<>();
            res.add(points[0]);
            return res;
        }
        int n = points.length;
        Arrays.sort(points,(a,b) -> a.y == b.y ? a.x-b.x:a.y-b.y);
        HashSet<ArrayList<Integer>> dup = new HashSet();
        List<Point> res = new ArrayList<>();
        Stack<Point> hull = new Stack<>();
        hull.push(points[0]);
        hull.push(points[1]);
        // Graham scan; 
        for (int i = 2; i < n; i++) {
            Point top = hull.pop();
            while (!hull.isEmpty() && ccw(hull.peek(), top, points[i]) < 0) {
                top = hull.pop();
            }
            hull.push(top);
            hull.push(points[i]);
        }
        for(int i = n-2;i>=0;i--) {
            Point top = hull.pop();
            while (!hull.isEmpty() && ccw(hull.peek(), top, points[i]) < 0) {
                top = hull.pop();
            }
            hull.push(top);
            hull.push(points[i]);
        }
        for(Point x: hull) {
            ArrayList<Integer> tmp = new ArrayList<>();
            tmp.add(x.x);tmp.add(x.y);
            if(dup.contains(tmp)) continue;
            dup.add(tmp);
            res.add(x);
        }
        return res;
    }
    public int ccw(Point a, Point b, Point c) {
        double area2 = (b.x-a.x)*(c.y-a.y) - (b.y-a.y)*(c.x-a.x);
        if      (area2 < 0) return -1;
        else if (area2 > 0) return +1;
        else                return  0;
    }
}
