class Solution:
    def outerTrees(self, trees: List[List[int]]) -> List[List[int]]:
        yToX = {}
        for x,y in trees:
            if y not in yToX:
                yToX[y] = [x]
            else:
                yToX[y].append(x)
        for y in yToX:
            yToX[y].sort()
        lowerYToHigherY = sorted(yToX.keys())

        leftresults = []
        for y in lowerYToHigherY:
            curx = yToX[y][0]
            if len(leftresults) < 2:
                leftresults.append((curx, y))
            else:
                lastx, lasty = leftresults[-1]
                lastlastx, lastlasty = leftresults[-2]
                lastdx, lastdy = lastx-lastlastx, lasty-lastlasty
                curdx, curdy = curx-lastlastx, y-lastlasty
                while curdx/curdy < lastdx/lastdy:
                    leftresults.pop()
                    if len(leftresults) >= 2:
                        lastx, lasty = leftresults[-1]
                        lastlastx, lastlasty = leftresults[-2]
                        lastdx, lastdy = lastx-lastlastx, lasty-lastlasty
                        curdx, curdy = curx-lastlastx, y-lastlasty
                    else:
                        break
                leftresults.append((curx, y))
        rightresults = []
        for y in lowerYToHigherY:
            curx = yToX[y][-1]
            if len(rightresults) < 2:
                rightresults.append((curx, y))
            else:
                lastx, lasty = rightresults[-1]
                lastlastx, lastlasty = rightresults[-2]
                lastdx, lastdy = lastx-lastlastx, lasty-lastlasty
                curdx, curdy = curx-lastlastx, y-lastlasty
                while curdx/curdy > lastdx/lastdy:
                    rightresults.pop()
                    if len(rightresults) >= 2:
                        lastx, lasty = rightresults[-1]
                        lastlastx, lastlasty = rightresults[-2]
                        lastdx, lastdy = lastx-lastlastx, lasty-lastlasty
                        curdx, curdy = curx-lastlastx, y-lastlasty
                    else:
                        break
                rightresults.append((curx, y))
        res = leftresults[1:-1] + rightresults[1:-1]
        lowest = [(x,lowerYToHigherY[0]) for x in yToX[lowerYToHigherY[0]]]
        highest = [(x,lowerYToHigherY[-1]) for x in yToX[lowerYToHigherY[-1]]]
        res += lowest + highest
        res = set(res)
        return [list(x) for x in res]
