class Solution {
public:
    int clockwise(vector<int>& p1, vector<int>& p2, vector<int>& p3) {
        int x1, y1, x2, y2, x3, y3;
        x1 = p1[0];
        y1 = p1[1];
        x2 = p2[0];
        y2 = p2[1];
        x3 = p3[0];
        y3 = p3[1];
        return ((y3-y2)*(x2-x1) - (y2-y1)*(x3-x2));
    }
