/***
 * Main purpose of slope class is to provide easy less than/greater than 
 * comparisons.
 ***/
struct Slope {
    m_y: i32,
    m_x: i32,
}

impl Slope {
    pub fn new(coord_x: i32, coord_y:i32) -> Self {
        return Slope {
            m_x: coord_x,
            m_y: coord_y,
        };
    }

    pub fn set(&mut self, x: i32, y: i32) -> () {
        self.m_x = x;
        self.m_y = y;
    }

    /***
     * Is Slope left < Slope right
     * left: yL/xL, right: yR/xR
     * yL/xL < yR/xR iff
     * yL*xR < yR*xL
     ***/
    pub fn lt(lhs: &Self, rhs: &Self) -> bool {
        if (rhs.m_x < 0) ^ (lhs.m_x < 0) {
            return lhs.m_y * rhs.m_x > rhs.m_y * lhs.m_x;
        }

        return lhs.m_y * rhs.m_x > rhs.m_y * lhs.m_x
    }

    pub fn gt(lhs: &Self, rhs: &Self) -> bool {
        if (rhs.m_x < 0) ^ (lhs.m_x < 0) {
            return lhs.m_y * rhs.m_x < rhs.m_y * lhs.m_x;
        }

        return lhs.m_y * rhs.m_x < rhs.m_y * lhs.m_x;
    }
}

impl Clone for Slope {
    fn clone(&self) -> Self {
        return Self {
            m_x: self.m_x,
            m_y: self.m_y
        };
    }
}
