        trees.sortWith(compareBy({ it[0] }, { it[1] }))
        val structuredTrees = trees.groupBy { it[0] }.values.toTypedArray()

        val upper = ConvexTrees()
        val lower = ConvexTrees()

        for (i in structuredTrees.indices) {
            upper.add(structuredTrees[i].last())
        }
        for (i in structuredTrees.indices.reversed()) {
            lower.add(structuredTrees[i].first())
        }

        return mutableSetOf<IntArray>().apply {
            addAll(structuredTrees.first())
            addAll(structuredTrees.last())
            addAll(upper.trees)
            addAll(lower.trees)
        }.toTypedArray()
    }
}
