class Solution {
private:
    int calculate(vector<int> &p1, vector<int> &p2, vector<int> &p3){
        int x1 = p1[0], x2 = p2[0], x3 = p3[0];
        int y1 = p1[1], y2 = p2[1], y3 = p3[1];

        // m23 - m12
        // (y3-y2)/(x3-x2) - (y2-y1)/(x2-x1)
        return (y3 - y2)*(x2 - x1) - (y2 - y1)*(x3 - x2);
    }
public:
    vector<vector<int>> outerTrees(vector<vector<int>>& trees) {
        sort(trees.begin(), trees.end());
        vector<vector<int>> upper, lower;
        for(auto i : trees){
            int l = lower.size();
            int u = upper.size();
            vector<int> point = {i[0], i[1]};
            // calculate(p1, p2, p3) should be > 0 for valid case for upper part
            //     p2
            //     .
            // .         .
            // p1        p3
            while(u >= 2 && calculate(upper[u-2], upper[u-1], point) < 0){
                u--;
                upper.pop_back();
            }
            // calculate(p1, p2, p3) should be < 0 for valid case for lower part
            //        p1           p3
            //         .           .
            //              .
            //             p2
            while(l >= 2 && calculate(lower[l-2], lower[l-1], point) > 0){
                l--;
                lower.pop_back();
            }
            upper.push_back(point);
            lower.push_back(point);
        }
        set<vector<int>> st;
        for(auto i : upper){
            st.insert(i);
        }
        for(auto i : lower){
            st.insert(i);
        }
        vector<vector<int>> res(st.begin(), st.end());
        return res;
    }
};
