        for(auto i : trees){
            int l = lower.size();
            int u = upper.size();
            vector<int> point = {i[0], i[1]};
            // calculate(p1, p2, p3) should be > 0 for valid case for upper part
            //     p2
            //     .
            // .         .
            // p1        p3
            while(u >= 2 && calculate(upper[u-2], upper[u-1], point) < 0){
                u--;
                upper.pop_back();
            }
            // calculate(p1, p2, p3) should be < 0 for valid case for lower part
            //        p1           p3
            //         .           .
            //              .
            //             p2
            while(l >= 2 && calculate(lower[l-2], lower[l-1], point) > 0){
                l--;
                lower.pop_back();
            }
            upper.push_back(point);
            lower.push_back(point);
        }
        set<vector<int>> st;
        for(auto i : upper){
            st.insert(i);
        }
        for(auto i : lower){
            st.insert(i);
        }
        vector<vector<int>> res(st.begin(), st.end());
        return res;
    }
