class Solution {
public:
    struct pt{
        double x,y;
    };

    static int orient(pt a, pt b, pt c){
        double v=a.x*(b.y-c.y)+b.x*(c.y-a.y)+c.x*(a.y-b.y);
        if(v<0) return -1;
        if(v>0) return 1;
        return 0;
    }

    static bool cw(pt a, pt b, pt c, bool in_collinear){
        int o=orient(a,b,c);
        return o<0 || (in_collinear && o==0);
    }

    static bool collinear(pt a, pt b, pt c) { return orient(a,b,c)==0; }

    vector<pt> convex_hull(vector<pt>& a, bool in_collinear=true){
        pt p0=*min_element(a.begin(),a.end(),[](pt a, pt b){
            return make_pair(a.y,a.x) < make_pair(b.y,b.x);
        });
        sort(a.begin(),a.end(), [&p0](const pt& a, const pt& b){
            int o=orient(p0,a,b);
            if(o==0)
                return (p0.x-a.x)*(p0.x-a.x)+(p0.y-a.y)*(p0.y-a.y) < (p0.x-b.x)*(p0.x-b.x)+(p0.y-b.y)*(p0.y-b.y);

            return o<0;
        });
        if(in_collinear){
            int i=(int)a.size()-1;
            while(i>=0 && collinear(p0,a[i],a.back()))
                i--;
            reverse(a.begin()+i+1,a.end());
        }
        vector<pt> st;
        for(int i=0; i<(int)a.size(); i++){
            while(st.size()>1 && !cw(st[st.size()-2],st.back(),a[i], true))
                st.pop_back();
            st.push_back(a[i]);
        }
        a=st;
        return st;
    }

    vector<vector<int>> outerTrees(vector<vector<int>>& trees) {
        vector<pt> a;
        for(auto& v : trees){
            pt point {};
            point.x=v[0];
            point.y=v[1];
            a.push_back(point);
        }
        vector<pt> ans = convex_hull(a,true);
        vector<vector<int>> res;
        for(auto& x : ans){
            vector<int> t;
            t.push_back(x.x);
            t.push_back(x.y);
            res.push_back(t);
        }
        return res;
    }
};
