type Point = [number, number]

// function outerTrees(trees: number[][]): number[][] {
function outerTrees(trees: Point[]): number[][] {
  if (trees.length < 3) {
    return trees
  }

  const sortedTrees = trees.concat().sort(sortTrees)
  // console.debug({ sortedTrees })

  const lower: Point[] = []
  const upper: Point[] = []

  for (const tree of sortedTrees) {
    while (
      lower.length >= 2 && 
      cmpTrees(lower[lower.length-2], lower[lower.length-1], tree) > 0
    ) {
      // console.debug('Lower pop')
      lower.pop()
    }

    while (
      upper.length >= 2 && 
      cmpTrees(upper[upper.length-2], upper[upper.length-1], tree) < 0
    ) {
      // console.debug('Upper pop')
      upper.pop()
    }

    lower.push(tree)
    upper.push(tree)
    // console.debug({ lower, upper })
  }

  const solution = new Set([...lower, ...upper])
  // console.log({ solution })
  return Array.from(solution)
};

function cmpTrees(p1: Point, p2: Point, p3: Point): number {
  const [x1, y1] = p1
  const [x2, y2] = p2
  const [x3, y3] = p3
  return ((y3 - y2) * (x2 - x1)) - ((y2 - y1) * (x3 - x2))
}

function sortTrees(p1: Point, p2: Point): number {
  const [x1, y1] = p1
  const [x2, y2] = p2
  return x1 !== x2 ? x1 - x2 : y1 - y2
}
