void recurse(int** trees,short left,short right,int** output, int* returnSize, bool mode){
    if (left+1==right || trees[left][0]==trees[right][0]) return;//if no points to add, or all on a vertical line
    float slope=(float)(trees[right][1]-trees[left][1])/(float)(trees[right][0]-trees[left][0]);
    float far=(mode?-1:1);
    int farInd;
    for (short i=left+1;trees[i][0]<trees[right][0];i++){//finds tree furthest from line between tree[left] and tree[right]
        if (trees[i][0]==trees[left][0]) continue;//guarunteed not to add
        float temp=slope*(trees[left][0]-trees[i][0])-trees[left][1]+trees[i][1];//modified distance-to-line formula
        if ((temp>far && mode) || (temp<far && !mode)){
            far=temp;
            farInd=i;
        }
    }
    if ((far>=0 && mode) || (far<=0 && !mode)){//mode 1: top of rope, mode 2: bottom
        output[(*returnSize)++]=trees[farInd];
        recurse(trees,left,farInd,output,returnSize,mode);//check left of new anchor point for more anchor points
        recurse(trees,farInd,right,output,returnSize,mode);//check right of new anchor point
    }
}
