bool clockwise(pair<int,int>&p1,pair<int,int>&p2,pair<int,int>&p3)
{
return p1.first*(p2.second-p3.second)+p2.first*(p3.second-p1.second)+p3.first*(p1.second-p2.second)>0;
}
bool anticlockwise(pair<int,int>&p1,pair<int,int>&p2,pair<int,int>&p3)
{
return p1.first*(p2.second-p3.second)+p2.first*(p3.second-p1.second)+p3.first*(p1.second-p2.second)<0;
}
bool collinear(pair<int,int>&p1,pair<int,int>&p2,pair<int,int>&p3)
{
return p1.first*(p2.second-p3.second)+p2.first*(p3.second-p1.second)+p3.first*(p1.second-p2.second)==0;
}
  // ercec tthe fence  --convex hull
bool cmp(pair<int,int>&p1,pair<int,int>&p2){
