class Solution {
public:
    vector<vector<pair<int,int>>> tree_v;
    set<pair<int,int>> ret_set;
    vector<vector<int>> ret_v;

    void insert_all_trees(int idx){
        vector<pair<int,int>>& n = tree_v[idx];
        for(int i=0;i<n.size();i++) ret_set.insert(n[i]);
    }

    vector<vector<int>> outerTrees(vector<vector<int>>& trees) {

        tree_v.clear();
        ret_set.clear();
        ret_v.clear();

        sort(trees.begin(), trees.end());

        int prev_x = -1;
        for(int i=0;i<trees.size();i++){
            if(prev_x != trees[i][0]) tree_v.push_back({});
            tree_v[tree_v.size()-1].push_back({trees[i][0], trees[i][1]});
            prev_x = trees[i][0];
        }
        sort(tree_v.begin(), tree_v.end()); // sort array

        insert_all_trees(0); // insert all vertexies first x-axis
        for(int a=0;a<2;a++){
            int i=0;
            int treev_size = tree_v.size();
            while(i<treev_size-1){
                pair<int,int>& big = a==0?tree_v[i][tree_v[i].size()-1]:tree_v[i][0];
                double ang = -INT_MAX;
                int f_idx = i;

                for(int j=i+1;j<treev_size;j++){
                    pair<int,int>& cmp = a==0?tree_v[j][tree_v[j].size()-1]:tree_v[j][0];
                    double tmp_ang = a==0?(double)(cmp.second - big.second)/(double)(cmp.first - big.first):(double)(cmp.second - big.second)/(double)(big.first - cmp.first);
                    if(ang < tmp_ang) {
                        ang = tmp_ang;
                        f_idx = j;
                    }
                }
                if(a==0) ret_set.insert(tree_v[f_idx][tree_v[f_idx].size()-1]);
                else ret_set.insert(tree_v[f_idx][0]);

                i = f_idx;
            }
        }
        insert_all_trees(tree_v.size()-1); // insert all vertexies last x-axis

        for(auto it = ret_set.begin();it!=ret_set.end();++it) ret_v.push_back({it->first, it->second});

        return ret_v;
    }
};





