class Solution {
    //If the number is positive then ab is further than ac
    public int distance(int[] a, int[] b, int[] c){
        int ab_x = b[0] - a[0];
        int ab_y = b[1] - a[1];
        int ac_x = c[0] - a[0];
        int ac_y = c[1] - a[1];

        return (ab_x * ab_x + ab_y * ab_y) - (ac_x * ac_x + ac_y * ac_y);
    }

    //Check the cross product to see if ac is left to ab
    //if point is toward the left the return value of the crossproduct is positive
    public int crossProduct(int[] a, int[] b, int[] c){
        int ab_x = b[0] - a[0];
        int ab_y = b[1] - a[1];
        int ac_x = c[0] - a[0];
        int ac_y = c[1] - a[1];

        //[ab_x, ab_y], [ac_x, ac_y]
        return (ab_x * ac_y) - (ab_y * ac_x);
    }

    public int[][] outerTrees(int[][] trees) {
        Set<int[]> ans = new HashSet<>();

        int[] start = trees[0];
        //find the leftmost point

        for(int i = 0; i < trees.length; i++){
            if(trees[i][0] < start[0]){
                start = trees[i];
            }else if(trees[i][0] == start[0]){
                if(trees[i][1] < start[1]){
                    start = trees[i];
                }
            }
        }

        ans.add(start);
        int[] curr = start;
        //Now we will do a convex hull sweep to check for next coord
        while(true){
            int[] currbest = trees[0];
            ArrayList<int[]> collinear = new ArrayList<>();

            for(int i = 1; i < trees.length; i++){
                int[] contestant = trees[i];
                if(curr == contestant){
                    continue;
                }

                if(crossProduct(curr, currbest, contestant) > 0){
                    currbest = contestant;
                    collinear = new ArrayList<>();
                }else if(crossProduct(curr, currbest, contestant) == 0){
                    if(distance(curr, currbest, contestant) > 0){
                        collinear.add(contestant);
                    }else{
                        collinear.add(currbest);
                        currbest = contestant;
                    }
                }
            }

            //Now we will add the collinear points
            for(int[] col: collinear){
                ans.add(col);
            }

            //then we will check whether we reached the starting point
            if(currbest == start){
                break;
            }

            ans.add(currbest);
            curr = currbest;
        }

        int len = ans.size();
        int[][] toret = new int[len][];
        int idx = 0;
        for(int[] entry:ans){
            toret[idx++] = entry;
        }
        return toret;
    }
}
