class Solution {
    int convexhull(vector<int> &a,vector<int> &b, vector<int> &c){
        int res=(b[0]-a[0])*(c[1]-a[1])-(b[1]-a[1])*(c[0]-a[0]);
        if(res==0)
            return 0;
        if(res>0)
            return 1;
        return -1;
    }
public:
    vector<vector<int>> outerTrees(vector<vector<int>>& trees) {
        if(trees.size()<4)
            return trees;
        vector<vector<int>> res;
        auto left=*min_element(trees.begin(),trees.end());
        res.push_back(left);
        unordered_map<int,bool> visited;
        visited[min_element(trees.begin(),trees.end())-trees.begin()]=true;
        while(res.size()<trees.size()){
            vector<int> cur;
            int cx;
            for(int x=0;x<trees.size();x++){
                if(!visited[x]){
                    cur=trees[x];
                    cx=x;
                    break;
                }
            }
            if(cur==res.back())
                cur=trees[1];
            for(int x=0;x<trees.size();x++){
                if(visited[x])
                    continue;
                int r=convexhull(res.back(),cur,trees[x]);
                if(r==-1 or (r==0 and 
                (
                    abs(res.back()[0]-trees[x][0])<abs(res.back()[0]-cur[0])
                    or
                    abs(res.back()[1]-trees[x][1])<abs(res.back()[1]-cur[1])
                ))){
                    cx=x;
                    cur=trees[x];
                }
            }
            if(convexhull(res.back(),cur,left)==-1)
                return res;
            res.push_back(cur);
            visited[cx]=true;
        }
        return res;
    }
};
