            visited[i] = true;
            int idx = trees.length;
            double slope = Integer.MAX_VALUE;
            for(int j = i +1; j< trees.length; j++){
                double s = slopeIs(trees[i], trees[j]);
                if(s < slope){
                    slope = s;
                    idx = j;
                }
            }
            i = idx;
        }

        for(int i = trees.length -1; i>= 0;){
            visited[i] = true;
            int idx = -1;
            double slope = Integer.MAX_VALUE;
            for(int j = i-1; j>= 0; j--){
                double s = slopeIs(trees[i], trees[j]);
                if(s < slope){
                    slope = s;
                    idx = j;
                }
            }
            i = idx;
        }

        int counter = 0;
        for(boolean i: visited){
            if(i){
                counter++;
            }
        }
        int[][] ans = new int[counter][2];
        int j = 0;
        for(int i = 0; i < trees.length; i++){
            if(visited[i]){
                ans[j++] = trees[i];
            }
        }
        return ans;
    }

    public double slopeIs(int[] p1, int[] p2){
        if(p1[0] == p2[0]){
            //  Edge case when the slope of two points is not defined
            //  i.e. there line lies parallel to the y axis.
            //  TC = [[1,4],[1,1],[2,5],[3,4],[3,1]]
            return Integer.MIN_VALUE;
        }
        return (double)(p2[1] - p1[1])/(p2[0] - p1[0]);
    }

