    private void print2(int[][] arr){
        for(int i = 0; i < arr.length; i++){
            System.out.print(arr[i][1] + " ");
        }
        System.out.println();
    }
    private void print(int[][] arr){
        for(int i = 0; i < arr[0].length; i++){
            System.out.print(arr[0][i] + " ");
        }
        System.out.println();
        for(int i = 0; i < arr[0].length; i++){
            System.out.print(arr[1][i] + " ");
        }
        System.out.println();
    }
}
