class Solution {
public:
    int cross(vector <int> &a, vector <int> &b, vector <int> &c) {
        return (b[0] - a[0]) * (c[1] - a[1]) - (c[0] - a[0]) * (b[1] - a[1]);
    }
    vector<vector<int>> outerTrees(vector<vector<int>>& a) {
        // get lowest, leftmost atan2
        // get leftmost, lowest instead? atan!
        int i = 0, n = a.size();
        for (int j = 1; j < n; j++) 
            if (a[i][0] > a[j][0] || (a[i][0] == a[j][0] && a[i][1] > a[j][1]))
                swap(a[i], a[j]);

        auto z = a[0]; // remember the point
        for (auto &x : a) { 
            x[0] -= z[0]; // center everything into 0,0
            x[1] -= z[1]; // for the sorting
        }
        // sort by angle and distance, the O(NlogN)
        // 1/0.0 = inf ... atan(inf) = pi/2
        sort(a.begin() + 1, a.end(), [](const auto &x, const auto &y) {
            // fixed point pseudo angles instead of doubles and atan
            int xx = x[0] == 0 ? INT_MAX : (x[1] * 1000000) / x[0];
            int yy = y[0] == 0 ? INT_MAX : (y[1] * 1000000) / y[0];
            // would manhattan work? it does!
            int dx = abs(x[1]) + abs(x[0]); // don't need to calculate 
            int dy = abs(y[1]) + abs(y[0]); // actual magnitude ||x||
            return xx < yy || (xx == yy && dx > dy);
        });

        // sorting by distance isn't optimal everywhere
        // here we reverse the first ones **
        int k = 1;
        while (k + 1 < n && cross(a[i], a[k], a[k + 1]) == 0)
            k++;
        reverse(a.begin() + 1, a.begin() + k + 1);

        // just pick the circle, stack
        // push good points and pop bad points
        for (int j = 1; j < n; j++) {
            swap(a[++i], a[j]); // push all into a stack
            while (cross(a[i - 1], a[i], a[(j + 1) % n]) < 0)
                i--; // pop bad from stack
        }
        a.resize(i + 1); // remove extras
        for (auto &x : a) { 
            x[0] += z[0]; // recover original points
            x[1] += z[1]; 
        }
        return a;
    }
};
