 //function to  find cross product the result will tell which way the two vectors turn
var calculate = function(A,B,C)
{

    Ax = A[0] - B[0]
    Ay = A[1] - B[1]
    Bx = C[0] - B[0]
    By = C[1] - B[1]

    return Ax*By - Ay*Bx    
}

//if cross product less than zero that means we turn left else right
var findLeft = function(convexHull,C)
{

    while(convexHull.length >=2 && calculate(convexHull[convexHull.length-2],convexHull[convexHull.length-1],C) < 0)
    {   
        convexHull.pop()
    }
    convexHull.push(C)
    return convexHull
}


var outerTrees = function(trees) {

    trees.sort(function(a,b){                 //start by sorting the trees
    if( a[0] == b[0]) return a[1]-b[1];
        return a[0]-b[0];
        })

    convexHullLower = []                       //we do two passes one upper and one lower and store it in seperate array
    convexHullUpper = []


    for(let i =0;i<trees.length;i++) {

        if(convexHullLower.length<2)
        {
            convexHullLower.push(trees[i])
        }            
        else
        {
                convexHullLower = findLeft(convexHullLower,trees[i])
        }

