        if(convexHullUpper.length<2)
        {
            convexHullUpper.push(trees[i])
        }        
        else
        {
                convexHullUpper = findLeft(convexHullUpper,trees[i])
        }
    }

    let convexHull = []
    convexHull.push(...convexHullLower)
    for(x in  convexHullUpper)                          //removig duplicates
    {
