class Solution {
    int cross(vector<int>&o,vector<int>&a,vector<int>&b){
        return (b[1]-o[1])*(a[0]-o[0])-(a[1]-o[1])*(b[0]-o[0]);
    }

    static bool equal(vector<int>&a,vector<int>&b){
        return a[0]==b[0] && a[1]==b[1];
    }

public:
    vector<vector<int>> outerTrees(vector<vector<int>>& trees) {
        int n = trees.size();

        auto cmp = [&](const vector<int>& a,const vector<int> &b){
            return a[0]<b[0] || (a[0]==b[0]) && a[1]<b[1];
        };
        sort(trees.begin(),trees.end(),cmp);
        //pehle sare points ko sort krlo
        // iske bad ek lower hull aur upper hull bna lo
        //aur ek final ans or hull bna lo jisme sab result store hoga
        vector<vector<int>>L(n),U(n),H;
        // set<vector<int>>s;
        int p = 0;

        // fir lower hull nikal l0
        // jab bhi 3 points ajay tab condition strt kro lgana while vali
        //while ki condition me ye horha ki tumhare pas teen points hai o,a,b
        //tum unka cross nikal lo(oa,ab) ka agr vo:-
        //1. negative ay matlab clockwise direction me move kr rhe tab us point ko nahi lena hai i.e a  vale ko
        //2.agar +ve ay matlab anticlockwise direction aur usko fir respective hull me add krdo
        for(int i=0;i<n;i++){
            while(p>=2 && cross(L[p-2],L[p-1],trees[i])<0) p--;
            L[p++]=trees[i];
        }
        L.resize(p);//jitne points ho us hisab se resize krdo
        p=0;

        //same process upper hull ke lye rhegi last point se chalu krenge
        for(int i=n-1;i>=0;i--){
            while(p>=2 && cross(U[p-2],U[p-1],trees[i])<0) p--;
            U[p++]=trees[i];
        }
        U.resize(p);

        //ab dono hull me sirf unique values ko hi lenge aur final hull me push kra dnge
        for(auto it:L)H.push_back(it);
        for(auto it:U)H.push_back(it);

        sort(H.begin(),H.end(),cmp);
        H.erase(unique(H.begin(),H.end(),equal),H.end());
        return H;
    }
};
