```cpp
  vector<TreeNode*> generateTrees(int n) {
    std::vector<TreeNode*> ret;

    std::vector<std::vector<std::vector<int>>> patterns(1, std::vector<std::vector<int>>(1));

    // Create a template for each level from 0 to n - 1. This lists all the unique patterns for that level. If you take a pattern 
    // and create a tree by inserting the digits in order, you will produce a unique tree that no other pattern creates
    for (int lvl = 1; lvl < n; ++lvl) {
      patterns.emplace_back();
      // For each possible root digit
      for (int digit = 1; digit <= lvl; ++digit) {
        // For each possible unique pattern that can be made using digits less than the root
        for (auto& left : patterns[digit - 1]) {
          // For each possible unique pattern that can be made using digits greater than the root
          for (auto& right : patterns[lvl - digit]) {
            patterns[lvl].emplace_back();
            patterns[lvl].reserve(left.size() + 1 + right.size());
            auto it = std::back_inserter(patterns.back().back());
            // Add root
            *it++ = digit;

            // Add left branch pattern
            it = std::copy(left.begin(), left.end(), it);

            // Add right branch pattern, but offsetting the numbers by the root value
            it = std::generate_n(it, right.size(), [digit, r_it = right.begin()] () mutable { return digit + *r_it++; });
          }
        }
      }
    }

    // We now have a template for each level 0 to n - 1. Time to build level n, but this time we must create it as
    // actual trees

    // For each possible root digit
    for (int digit = 1; digit <= n; ++digit) {
      // For each possible unique pattern that can be made using digits less than the root
      for (auto& left : patterns[digit - 1]) {
        // For each possible unique pattern that can be made using digits greater than the root
        for (auto& right : patterns[n - digit]) {
          ret.emplace_back(new TreeNode(digit));

          // Create left branch
          for (int left_digit : left) {
            TreeNode** curr = &ret.back()->left;
            while (*curr) {
              curr = (left_digit <= (**curr).val) ? &((**curr).left): &((**curr).right);
            }
            *curr = new TreeNode(left_digit);
          }

          // Create right branch, remembering to add the offset
          for (int right_digit : right) {
            TreeNode** curr = &ret.back()->right;
            while (*curr) {
              curr = (right_digit + digit <= (**curr).val) ? &((**curr).left): &((**curr).right);
            }
            *curr = new TreeNode(right_digit + digit);
          }
        }
      }
    } 

    return ret;
  }
