        def recurse(l, r):
            if l > r: return [None]
            trees = []
            for i in range(l, r+1):
                lefts = recurse(l, i-1)
                rights = recurse(i+1, r)

                for left in lefts:
                    for right in rights:
                        tree = TreeNode(i)
                        tree.left = left
                        tree.right = right
                        trees.append(tree)

            return trees

        return recurse(1, n)
