class Solution {

    public List<TreeNode> generateTrees(int n) {
        return treeBuilder(1, n);
    }

    private List<TreeNode> treeBuilder(int start, int end) {
        if(start > end) return Collections.emptyList();
        List<TreeNode> res = new ArrayList<>();
        // n == 1
        if(start == end) {
            res.add(new TreeNode(start));
            return res;
        }

        // n == 2
        if( start+1 == end) {
            res.add(new TreeNode(start, null, new TreeNode(start+1)));
            res.add(new TreeNode(start+1, new TreeNode(start), null));
            return res;
        }

        for(int i=start; i<=end; i++) {
            TreeNode newRoot = new TreeNode(i);
            List<TreeNode> left = treeBuilder(start, i-1); // build trees only using left elements
            List<TreeNode> right = treeBuilder(i+1, end); // build trees only using right elements

            if(left.size() == 0) { // No left subtrees possible
                for(TreeNode node : right) {
                    newRoot.right = node;
                    res.add(copyTree(newRoot));
                    newRoot.right = null;
                }
            } else if(right.size() == 0) { // No right subtrees possible
                for(TreeNode node : left) {
                    newRoot.left = node;
                    res.add(copyTree(newRoot));
                    newRoot.left = null;
                }
            } else {
				// For every left subtree; process every possibility of right subtree
				// This is analogous to taking a cartesian product of possibilities
                for (TreeNode node : left) {
                    newRoot.left = node;
                    for(TreeNode node1 : right) {
                        newRoot.right = node1;
                        res.add(copyTree(newRoot));
                        newRoot.right = null;
                    }
                    newRoot.left = null;
                }
            }

        }

        return res;
    }

    // Helper Function to deep copy a given tree
    private TreeNode copyTree (TreeNode head) {
        if(head == null) return head;
        TreeNode temp = new TreeNode(head.val);
        temp.left = copyTree(head.left);
        temp.right = copyTree(head.right);
        return temp;
    }
}
