/**
 * Definition for a binary tree node.
 * struct TreeNode {
 *     int val;
 *     TreeNode *left;
 *     TreeNode *right;
 *     TreeNode() : val(0), left(nullptr), right(nullptr) {}
 *     TreeNode(int x) : val(x), left(nullptr), right(nullptr) {}
 *     TreeNode(int x, TreeNode *left, TreeNode *right) : val(x), left(left), right(right) {}
 * };
 */
class Solution {
public:
    vector<TreeNode*> generateTrees(int n) {

        return helper( 1, n);
    }
    vector<TreeNode* > helper(  int start, int end){

        vector<TreeNode* > res; // to store list of unique bsts

        // eg: n=3, and current root=1;
        //left subtree ={1,....,0}                 right subtree= {2,......,3}
        //eg:      1
        //       /      \
        // (1,...,0)     (2,...,3)

        // you can see (1>0) in the left subtree(i.e., no nodes ) (thats why we need to push "NULL" and return)
        if( start > end){
            res.push_back(nullptr);
            return res;
        }

        // we will make every no. the current rooot and recursively call the left & right subtree
        for( int i=start; i<=end; i++){

            vector<TreeNode*> left_sub, right_sub;
            left_sub = helper(start, i-1);  // recursively make left subtree
            right_sub = helper(i+1, end); // recursively make right subtree


            // iterate through the alll possible left(values < i) & right(values>i) nodes and do the linkings
            // with the current node
            for( auto l: left_sub){
                for(auto r: right_sub){

                    //current root
                    TreeNode* curr_root= new TreeNode(i);

                    // link current root with left and right nodes(with every possible combinations)
                    curr_root->left = l;
                    curr_root->right = r;

                    res.push_back(curr_root); // push the rooot into the result list
                }
            }
        }

        return res;
    }
};

