public class MedianFinder {

    PriorityQueue<Integer> max;     //stores the first half of elements(as present in sorted array)
    PriorityQueue<Integer> min;     //stores the second half of elements
    public MedianFinder() {
        max = new PriorityQueue<>(Collections.reverseOrder());       //maxheap to store first half of elements
        min = new PriorityQueue<>();                                                     //minheap to store second half of elements
    }

    public void addNum(int num) {
        if(max.isEmpty() || num<max.peek()){
            max.add(num);       //if element lies in first half,i.e,if element is less than top of maxheap or if maxheap is empty, add to maxheap
        }else{
            min.add(num);       //else add to min heap
        }
        if(max.size()>(min.size()+1)){  //balance both heaps if difference of size greater than 1(max difference will be 2 in any case as it is balanced after adding every element)
            min.add(max.peek());max.poll();
        }else if(min.size()>(max.size()+1)){
            max.add(min.peek());min.poll();
        }
    }

    public double findMedian() {
        //System.out.println(max.size()+ " "+ min.size());
        if(max.size()==min.size()) return (double)(max.peek()+min.peek())/2;
        else if(max.size()>min.size()) return max.peek();
        else return min.peek();
    }
}
