        int parent, child;
        child = size - 1;
        while (child > 0) {
            parent = (child - 1) / 2;
            if (heap[parent] < heap[child])
                swap(&heap[parent], &heap[child]);
            child = parent;
        }
    }

    void siftDownMax(int *heap, int size) {
        int parent, child;
        parent = 0; child = 1;
        while (child < size) {
            if (child + 1 < size && heap[child] < heap[child + 1])
                child = child + 1;

            if (heap[parent] >= heap[child]) break;
            swap(&heap[parent], &heap[child]);
            parent = child;
            child = parent * 2 + 1;
        }
    }

