    if(this.left.getSize()>this.right.getSize()){
        return this.left.getTop();
    }else if(this.right.getSize()>this.left.getSize()){
        return this.right.getTop();
    }else{
        return (this.left.getTop()+this.right.getTop())/2;
    }
};

