///

class MedianFinder {
public:

    // Here we have taken two heap, Maxheap of size (n+1) and Minheap of size(n) 
    priority_queue<int> maxheap; // it is used to store the max ele
    priority_queue<int,vector<int>,greater<int>> minheap; // it is used to store minimum element
    MedianFinder() {
        //ignore this as this will not affect the code
    }

    void addNum(int num) {
        int lefts = maxheap.size();
        int rights = minheap.size();

        // case 1 = maxheap is smaller than minheap

        if(lefts == 0)     // if maxheap is empty then add it first 
            maxheap.push(num);

        // case 2 = if maxheap == minheap 

        else if(lefts == rights)
        {
            if( num < minheap.top()) // if element is smaller than the top of minheap we'll put it into maxheap
                maxheap.push(num);
            else
            {
                int temp = minheap.top();  // otherwise shift ele and push accordingly
                minheap.pop();
                maxheap.push(temp);
                minheap.push(num);
            }
        }

        //case 3 = if maxheap is greater than minheap

        else{
            if(num > maxheap.top()) // if num is greater than maxheap top so we'll put it into min heap
                minheap.push(num);
            else{
                int temp = maxheap.top(); // otherwise shift ele and push accordingly
                maxheap.pop();
                minheap.push(temp);
                maxheap.push(num);
            }
        }

    }

    double findMedian() {
        int lefts = maxheap.size(); // to get the maxheap size
        int rights = minheap.size(); // to get the size of minheap 
        if(lefts > rights)
            return maxheap.top(); // size is odd 
        else
            return (double)(maxheap.top() + minheap.top())/2; // size is even
    }
};

