    (*h)[0] = (*h)[len(*h)-1]
    (*h) = (*h)[:len(*h)-1]
    h.heapDown(0)

    return poppedItem
}

func (h *minHeap) pop() int {
    poppedItem := (*h)[0]

    (*h)[0] = (*h)[len(*h)-1]
    (*h) = (*h)[:len(*h)-1]
    h.heapDown(0)

    return poppedItem
}

func (h *minHeap) add(num int) {
    *h = append(*h, num)
    h.heapUp(len(*h)-1)
}

func (h *maxHeap) add(num int) {
    *h = append(*h, num)
    h.heapUp(len(*h)-1)
}

func (h *minHeap) heapDown(p int) {
    l, r := 2*p+1, 2*p+2
    smaller := p

    if l < len(*h) && (*h)[l] < (*h)[smaller] {
        smaller = l
    }

    if r < len(*h) && (*h)[r] < (*h)[smaller] {
        smaller = r
    }

    if smaller != p {
        (*h)[smaller], (*h)[p] = (*h)[p], (*h)[smaller]
        h.heapDown(smaller)
    }
}

func (h *minHeap) heapUp(p int) {
    parent := (p-1)/2

    if parent >= 0 && (*h)[p] < (*h)[parent] {
        (*h)[p], (*h)[parent] = (*h)[parent], (*h)[p]
        h.heapUp(parent)
    }
}

func (h *maxHeap) heapDown(p int) {
    l, r := 2*p+1, 2*p+2
    bigger := p 

    if l < len(*h) && (*h)[l] > (*h)[bigger] {
        bigger = l
    }

    if r < len(*h)&& (*h)[r] > (*h)[bigger] {
        bigger = r
    }

    if bigger != p {
        (*h)[bigger], (*h)[p] = (*h)[p], (*h)[bigger]
        h.heapDown(bigger)
    }
}

func (h *maxHeap) heapUp(p int) {
    parent := (p-1)/2

    if parent >= 0 && (*h)[p] > (*h)[parent] {
        (*h)[p], (*h)[parent] = (*h)[parent], (*h)[p]
        h.heapUp(parent)
    }
}
