};

MedianFinder.prototype.findMedian = function() {
  if (this.arr.length === 0) {
    return null;
  }
  let mid = this.arr.length / 2;
  if (this.arr.length % 2 === 0) {
    return ((this.arr[mid] + this.arr[mid - 1]) / 2);
  }
  return this.arr[Math.floor(mid)];
};
