from heapq import heappush, heappop


class MedianFinder:

    def __init__(self):
        """
        initialize your data structure here.
        """
        # for left half of the data stream we maintain a max heap
        self.left_max_heap = []
        # vice-versa
        self.right_min_heap = []

    def max_heap_push(self, x):
        heappush(self.left_max_heap, -x)

    def min_heap_push(self, x):
        heappush(self.right_min_heap, x)

    def max_heap_pop(self):
        return -heappop(self.left_max_heap)

    def min_heap_pop(self):
        return heappop(self.right_min_heap)

    @property
    def left_max_heap_size(self):
        return len(self.left_max_heap)

    @property
    def right_min_heap_size(self):
        return len(self.right_min_heap)

    def addNum(self, num: int) -> None:
        self.max_heap_push(num)
        self.min_heap_push(self.max_heap_pop())
        while self.left_max_heap_size < self.right_min_heap_size:
            self.max_heap_push(self.min_heap_pop())

    def findMedian(self) -> float:
        if self.left_max_heap_size > self.right_min_heap_size:
            return -self.left_max_heap[0]
        return (-self.left_max_heap[0] + self.right_min_heap[0]) / 2
