```
class TreeNode:
    def __init__(self, val):
        self.val = val
        self.right = self.left = None
        self.leftSize = 0


class BinarySearchTree:

    def __init__(self):
        self.root = None
        self.count = 0

    def _add(self, root, num):
        if not root:
            root = TreeNode(num)
            return root

        if num <= root.val: 
            root.left = self._add(root.left, num)
            root.leftSize += 1
        else:
            root.right = self._add(root.right, num)
        return root

    def add(self, num):
        self.root = self._add(self.root, num)
        self.count += 1

    def rank(self, k):
        return self._Rank(self.root, k)

    def _Rank(self, root,  k):

        if not root:
            return -1

        if root.leftSize == k:
            return root.val

        if root.leftSize > k:
            return self._Rank(root.left, k)

        else:
            return self._Rank(root.right, k - root.leftSize - 1)

class MedianFinder:

    def __init__(self):
        """
        initialize your data structure here.
        """
        self.bst = BinarySearchTree()

    def addNum(self, num: int) -> None:

        self.bst.add(num)

    def findMedian(self) -> float:

        #check the size of elements:
        size = self.bst.count
        if size % 2 == 0: #even number of nodes, return median of two middle            
            rank1 = self.bst.rank(size // 2)
            rank2 = self.bst.rank(size // 2 - 1)
            return (rank1 + rank2) / 2
        else:
            return self.bst.rank(size//2)
